/*Image Containers starts here*/
img[src*="dbu_imageLoader"] {
    object-fit: none !important;
}

.prodCard .image-container img {
    max-height: 100px;
    min-width: 100px;
    min-height: 100px;
    max-width: 100px;
    height: 100px;
    object-fit: contain;
    margin: 0 auto;
    display: block;
    transition: all .4s ease-in-out;
}

.prodCard:hover .image-container img {
    transform: scale(1.1);
}

.mcProdImg img {
    width: 54px;
    height: 54px;
    object-fit: contain;
}

.prodThumbs .imgCont img {
    border: 1px solid #939393;
    border-radius: 3px;
    width: 56px;
    height: 56px;
    object-fit: contain;
    display: block;
    cursor: pointer;
    background-color: #fff;
    transition: all .3s;
    padding: 5px;
}

.prodThumbs .imgCont:hover img,
.prodThumbs .imgCont .selected img {
    transform: scale(1.1);
    border: 1px solid #333333;
}

.product_Details .itemNamePop img {
    min-width: 76px;
    min-height: 76px;
    max-width: 76px;
    max-height: 76px;
    object-fit: contain;
    margin-right: 15px;
    background: #fff;
    border-radius: 3px;
}

