}

.resultBlock .pImg img {
    max-width: 144px;
    max-height: 144px;
    object-fit: contain;
    min-width: 144px;
    min-height: 144px;
    transition: all 0.4s;
}

.resultBlock:hover .pImg img {
    transform: scale(1.1);
}

.gridView .resultBlock:hover .pImg img {
